import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { createBook, fetchBook, updateBook } from '../api';

const BookForm = () => {
    const [bookData, setBookData] = useState({ title: '', author: '', genre: '', yearPublished: '' });
    const { id } = useParams();
    const history = useNavigate();

    useEffect(() => {
        if (id) {
            const getBook = async () => {
                const { data } = await fetchBook(id);
                setBookData(data);
            };

            getBook();
        }
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (id) {
            await updateBook(id, bookData);
        } else {
            await createBook(bookData);
        }

        history.push('/');
    };

    return (
        <div>
            <h1>{id ? 'Edit Book' : 'Add Book'}</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Title"
                    value={bookData.title}
                    onChange={(e) => setBookData({ ...bookData, title: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Author"
                    value={bookData.author}
                    onChange={(e) => setBookData({ ...bookData, author: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Genre"
                    value={bookData.genre}
                    onChange={(e) => setBookData({ ...bookData, genre: e.target.value })}
                    required
                />
                <input
                    type="number"
                    placeholder="Year Published"
                    value={bookData.yearPublished}
                    onChange={(e) => setBookData({ ...bookData, yearPublished: e.target.value })}
                    required
                />
                <button type="submit">{id ? 'Update' : 'Add'}</button>
            </form>
        </div>
    );
};

export default BookForm;
